var add=function(n,t){return n+t};document.addEventListener("DOMContentLoaded",function(n){var t=randomBetween(1,10),e=randomBetween(1,10),r=t+" + "+e+" = "+add(t,e),o=t+" + "+e+" = "+substract(t,e),a=t+" * "+e+" = "+multiply(t,e);document.getElementById("content").innerHTML=r+"<br>"+o+"<br>"+a});var randomBetween=function(n,t){return Math.floor(n+Math.random()*(t-n))},substract=function(n,t){return n-t},multiply=function(n,t){return n*t*5};